generated.css:
* {
	box-sizing: border-box;
}

html,
body {
	margin: 0;
	padding: 0;
	color: #000;
	font-family: Arial, Helvetica, sans-serif;
}

#mycase-login-btn,
#login-button input {
	background: #ff6341;
}

/* START DUDA WIDGET */
.mycase-btn {
	transition: 0.2s;
}

.mycase-btn:hover {
	cursor: pointer;
	box-shadow: inset 0 0 0 2px rgba(255, 255, 255, 0.5);
}

#mycase-login-btn {
	display: inline-block;
	padding: 10px 20px;
	text-decoration: none;
	color: #fff;
	font-size: 16px;
}

/*Modal Overlay*/
#modal {
	display: none;
	z-index: 9999;
	position: fixed;
	top: 0;
	left: 0;
	right: 0;
	bottom: 0;
	animation: fadeIn 0.5s;
}

#modal.show-modal {
	display: block;
}

.modal-overlay {
	position: fixed;
	background: rgba(0, 0, 0, 0.5);
	top: 0;
	left: 0;
	bottom: 0;
	right: 0;
	z-index: -1;
}

.body-fix {
	overflow-y: hidden !important;
}

#mycase-login-modal {
	background: #fff;
	box-shadow: 0 5px 15px rgba(0, 0, 0, 0.25);
	position: fixed;
	width: 350px;
	max-width: 95vw;
	max-height: 95vh;
	top: 50%;
	left: 50%;
	transform: translate(-50%, -50%);
	overflow-y: auto;
}

#mycase-login-title {
	background: #111;
	padding: 10px;
	display: flex;
	align-items: center;
}

#mycase-login-title h3 {
	margin: 0;
	text-align: center;
	flex-grow: 1;
	color: #eee !important;
	font-weight: normal;
}

a#close-modal {
	color: #666;
	background: 0;
	border: 0;
	font-size: 25px;
	line-height: 1;
	transition: 0.2s;
	padding: 0;
	text-decoration: none;
}

a#close-modal:hover {
	cursor: pointer;
	color: #aaa;
}

#mycase-login-form {
	padding: 15px 10px;
	border-bottom: 5px solid #ddd;
}

#login-form-table {
	width: 100%;
}

.login-label {
	font-size: 14px;
	color: #333;
	margin-bottom: 5px;
	text-align: left;
}

#login-email-field,
#login-password-field {
	margin-bottom: 10px;
}

#login-email-field input,
#login-password-field input {
	width: 100%;
	padding: 5px;
	font-size: 16px;
	background: #fafafa;
	border: 1px solid #ddd;
	box-shadow: inset 0 1px 2px rgba(0, 0, 0, 0.2);
}

#login-form-table td {
	display: block;
}

#login-form-table tr:nth-child(3) td:nth-child(1) {
	display: none;
}

#login-form-table tr:nth-child(3) td:nth-child(2) {
	margin-top: 5px;
}

#login-button {
	text-align: right;
}

#login-button input {
	display: inline-block;
	font-size: 16px;
	color: #fff;
	border: 0;
	height: 35px !important;
	width: 100px;
	transition: 0.2s;
}

#login-button input:hover {
	cursor: pointer;
	box-shadow: inset 0 0 0 2px rgba(255, 255, 255, 0.5);
}

a.forgot {
	position: absolute;
	bottom: 30px;
	left: 15px;
	font-size: 12px;
	text-decoration: none;
	color: #333;
	transition: 0.2s;
}

a.forgot:hover {
	text-decoration: underline;
}

@keyframes fadeIn {
	from {
		opacity: 0;
	}
	to {
		opacity: 1;
	}
}
