function getFirmUUID() {
  var firmUUID = data.config.firm_uuid.includes('<p class="rteBlock">') ? jQuery(data.config.firm_uuid).text() : data.config.firm_uuid;
  $.getScript('https://login.mycase.com/customer_websites/login_javascript.js').done(function () {
    $('#mycase-login-form').attr('data-firm-id', firmUUID);
  });
} //Show Modal


function showModal() {
  $('#modal').addClass('show-modal').appendTo($('body'));
  disableBodyScroll(document.querySelector('#modal'));
} //Close Modal


function closeModal() {
  $('#modal').removeClass('show-modal');
  enableBodyScroll(document.querySelector('#modal'));
} //Show modal on contact us button click


$(document).on('click', '#mycase-login-btn', function (e) {
  e.preventDefault();
  showModal();
}); //Close modal

$(document).on('click', '#close-modal', function (e) {
  e.preventDefault();
  closeModal();
});
$(document).on('click', '.modal-overlay', function () {
  closeModal();
});
